  Stats(districtHeaderBlock){
    this.districtHeaderBlock=districtHeaderBlock;
    this.mapsTodaysConfirmedCount=0;
    if(districtHeaderBlock!='TamilNadu' && this.datablock.length>0)
    {
        var stats=this.datablock.filter(t=>t.district==districtHeaderBlock);
        var previousStats=this.previousdatablock.filter(t=>t.district==districtHeaderBlock);
        
        if(stats!=null && stats!=undefined && stats.length>0)
        {         
         this.confirmedCount = _.first(stats).confirmed;       
        }
        else
        {
          this.confirmedCount=0;         
        }

        if(previousStats!=null && previousStats!=undefined && previousStats.length>0)
        {
          this.mapsTodaysConfirmedCount=(this.confirmedCount)>0?Math.abs(this.confirmedCount-_.first(previousStats).confirmed):this.mapsTodaysConfirmedCount;
        }
        
      
    }

  }

